import React, { useState, useEffect } from 'react';

import Box from '@mui/material/Box';
import Slider from '@mui/material/Slider';

export default function SliderSizes() {
	// on change => send out the event to server
	// Simple JSON?
	// {"one": 1-100, "two": 1-100, "three": .., "four": ..}

	const [data, setData] = useState({"one": 0});

	handlechange = name => (e, value) => {
		setData({
			[name]: value
		})
	}

  return (
    <Box width={300} marginTop={10}>
      <Slider
				defaultValue={25}
				aria-label="Default"
				valueLabelDisplay="auto"
				id='one'
				name='one'
				onChange={handlechange('one')}/>
			<Slider sx={{ mt: "1rem" }} defaultValue={25} aria-label="Default" valueLabelDisplay="auto" />
			<Slider sx={{ mt: "1rem" }} defaultValue={25} aria-label="Default" valueLabelDisplay="auto" />
			<Slider sx={{ mt: "1rem" }} defaultValue={25} aria-label="Default" valueLabelDisplay="auto" />
    </Box>
  );
}
